#include <iostream>
using namespace std;

// Function to sum two numbers
int sum(int a, int b) {
    return a + b;
}

int main() {
    int a, b;

    // Read two integers from standard input
    cin >> a >> b;

    // Call the sum function and display the result
    cout << sum(a, b) << endl;

    return 0;
}
